import React from 'react';
import styles from './Button.module.css';

interface ButtonProps {
  children?: any;
  disabled?: boolean;
  className?: string;
  id?: string;
  onClick?: () => void;
}
export const Button = ({
  children,
  disabled,
  className,
  id,
  onClick,
}: ButtonProps) => {
  return (
    <button
      disabled={disabled}
      id={id}
      className={`${styles.button} ${className}`}
      onClick={onClick}
    >
      {children}
    </button>
  );
};
import React from 'react';
import styles from './ToggleButton.module.css';

interface ButtonProps {
  children?: any;
  className?: string;
  id?: string;
  onClick?: (e: React.MouseEvent<HTMLButtonElement>) => void;
  checked?: boolean;
  disabled?: boolean;
}
export const ToggleButton = ({
  children,
  className,
  id,
  checked,
  onClick,
  disabled,
}: ButtonProps) => {
  if (checked) {
    return (
      <button
        id={id}
        className={`${styles.toggleButton} ${styles.checked} ${className}`}
        onClick={onClick}
      >
        {children}
      </button>
    );
  }
  return (
    <button
      id={id}
      disabled={disabled}
      className={`${styles.toggleButton} ${className}`}
      onClick={onClick}
    >
      {children}
    </button>
  );
  // return (
  //   // <div>
  //   //   <input
  //   //     disabled={disabled}
  //   //     type={type}
  //   //     checked={checked}
  //   //     className={className}
  //   //     id={id}
  //   //     onChange={onChange}
  //   //     onClick={onClick}
  //   //     value={value}
  //   //   />
  //   //   <label htmlFor={id}>{children}</label>
  //   // </div>
  // );
};
import React from 'react';
import styles from './ButtonGroup.module.css';

interface ButtonGroupProps {
  children?: any;
  toggle?: boolean;
  className?: string;
}
export const ButtonGroup = ({
  children,
  toggle,
  className,
}: ButtonGroupProps) => {
  return <div className={`${styles.buttonGroup} ${className}`}>{children}</div>;
};
import React from 'react';
import styles from './Navbar.module.css';

interface NavbarProps {
  children?: any;
  className?: string;
}
export const Navbar = ({ children, className }: NavbarProps) => {
  return <nav className={`${styles.navbar} ${className}`}>{children}</nav>;
};
import React from 'react';
import styles from '../ScoreEditor.module.css';

interface DropdownProps {
  children?: any;
  variant?: string;
  className?: string;
  id?: string;
  onChange?: (e: any) => void;
}
export const Dropdown = ({
  children,
  variant,
  className,
  id,
  onChange,
}: DropdownProps) => {
  return (
    <form className={styles.form} onChange={onChange}>
      <select id={id} className={className}>
        {children}
      </select>
    </form>
  );
};
import convert from 'xml-js';
import React from 'react';
const ReactDomServer = require('react-dom/server');

export const xmlString = (measures: any) => {
  return `
<?xml version="1.0" encoding="UTF-8"?>
<!DOCTYPE score-partwise PUBLIC "-//Recordare//DTD MusicXML 2.0 Partwise//EN" "http://www.musicxml.org/dtds/partwise.dtd">

<score-partwise version="2.0">
    <movement-title>Test</movement-title>
    <identification>
        <encoding>
            <software>Caesura</software>
            <encoding-date>2020-10-10</encoding-date>
            <supports attribute="new-system" element="print" type="yes" value="yes"/>
            <supports attribute="new-page" element="print" type="yes" value="yes"/>
        </encoding>
    </identification>
    <defaults>
        <scaling>
            <millimeters>7.540625</millimeters>
            <tenths>40</tenths>
        </scaling>
        <page-layout>
            <page-height>1481</page-height>
            <page-width>1145</page-width>
            <page-margins type="both">
                <left-margin>58</left-margin>
                <right-margin>58</right-margin>
                <top-margin>66</top-margin>
                <bottom-margin>66</bottom-margin>
            </page-margins>
        </page-layout>
        <system-layout>
            <system-margins>
                <left-margin>225</left-margin>
                <right-margin>0</right-margin>
            </system-margins>
            <system-distance>96</system-distance>
            <top-system-distance>96</top-system-distance>
        </system-layout>
        <staff-layout>
            <staff-distance>0</staff-distance>
        </staff-layout>
    </defaults>
    <part-list>
        <score-part id="P1">
            <part-name>Grand Piano</part-name>
        </score-part>
    </part-list>
    <part id="P1">
      ${measures}
    </part>
</score-partwise>
`;
};

export interface Pitch {
  step: string;
  alter: string;
  octave: number;
}

export interface Notations {
  tied?: 'start' | 'stop';
}

export interface Measure {
  index: number;
  notes: Array<Note>;
  attributes: Attributes | null;
}

export interface Note {
  pitch: Pitch;
  duration: number;
  type: string;
  chord?: boolean;
  rest?: boolean;
  dotted?: boolean;
  beam?: 'begin' | 'end';
  tie?: 'start' | 'stop';
  notations?: Notations;
}

export interface Key {
  fifths: number;
  mode: string;
}
export interface Time {
  beats: number;
  beatType: number;
}
export interface Clef {
  sign: string;
  line: number;
}

export interface Attributes {
  divisions: number;
  key: Key;
  time: Time;
  clef: Clef;
}

const Measure = (props) => React.createElement('measure', props);
const Print = (props) => React.createElement('print', props);
const SystemLayout = (props) => React.createElement('system-layout', props);
const SystemMargins = (props) => React.createElement('system-margins', props);
const LeftMargin = (props) => React.createElement('left-margin', props);
const RightMargin = (props) => React.createElement('right-margin', props);
const TopSystemDistance = (props) =>
  React.createElement('top-system-distance', props);
const MeasureNumbering = (props) =>
  React.createElement('measure-numbering', props);

export const measureToXML = (measure: Measure) => {
  return ReactDomServer.renderToStaticMarkup(
    <Measure number={measure.index}>
      {/* <Print page-number="1">
        <SystemLayout>
          <SystemMargins>
            <LeftMargin>225</LeftMargin>
            <RightMargin>0</RightMargin>
          </SystemMargins>
          <TopSystemDistance>96</TopSystemDistance>
        </SystemLayout>
        <MeasureNumbering>none</MeasureNumbering>
      </Print> */}
      {measure.attributes && attributesToXML(measure.attributes)}
      {measure.notes.map((note) => noteToXML(note))}
    </Measure>
  );
};

const Note = (props) => React.createElement('note', props);
const Chord = (props) => React.createElement('chord', props);
const Pitch = (props) => React.createElement('pitch', props);
const Step = (props) => React.createElement('step', props);
const Alter = (props) => React.createElement('alter', props);
const Octave = (props) => React.createElement('octave', props);
const Duration = (props) => React.createElement('duration', props);
const Voice = (props) => React.createElement('voice', props);
const Type = (props) => React.createElement('type', props);
const Tie = (props) => React.createElement('tie', props);
const Stem = (props) => React.createElement('stem', props);
const Staff = (props) => React.createElement('staff', props);

const Notations = (props) => React.createElement('notations', props);
const Dot = (props) => React.createElement('dot', props);
const Rest = (props) => React.createElement('rest', props);
const Tied = (props) => React.createElement('tied', props);
const Beam = (props) => React.createElement('beam', props);

export const noteToXML = (note: Note) => {
  return (
    <Note>
      {note.chord && <Chord />}
      <Pitch>
        <Step>{note.pitch.step}</Step>
        <Alter>{note.pitch.alter}</Alter>
        <Octave>{note.pitch.octave}</Octave>
      </Pitch>
      {note.rest && <Rest />}
      <Duration>{note.duration}</Duration>
      <Voice>1</Voice>
      {note.dotted && <Dot />}
      <Type>{note.type}</Type>
      <Beam number="1">{note.beam}</Beam>
      <Tie type={note.tie} />
      <Stem>up</Stem>
      <Staff>1</Staff>
      {note.notations ? (
        <Notations>
          <Tied orientation="over" type={note.notations.tied} />
        </Notations>
      ) : null}
    </Note>
  );
};

const Attributes = (props) => React.createElement('attributes', props);
const Divisions = (props) => React.createElement('divisions', props);
const Key = (props) => React.createElement('key', props);
const Fifths = (props) => React.createElement('fifths', props);
const Mode = (props) => React.createElement('mode', props);
const Time = (props) => React.createElement('time', props);
const Beats = (props) => React.createElement('beats', props);
const BeatType = (props) => React.createElement('beat-type', props);
const Clef = (props) => React.createElement('clef', props);
const Sign = (props) => React.createElement('sign', props);
const Line = (props) => React.createElement('line', props);

export const attributesToXML = (attributes) => {
  return (
    <Attributes>
      <Divisions>{attributes.divisions}</Divisions>
      <Key>
        <Fifths>{attributes.key.fifths}</Fifths>
        <Mode>{attributes.key.mode}</Mode>
      </Key>
      <Time>
        <Beats>{attributes.time.beats}</Beats>
        <BeatType>{attributes.time.beatType}</BeatType>
      </Time>
      <Clef>
        <Sign>{attributes.clef.sign}</Sign>
        <Line>{attributes.clef.line}</Line>
      </Clef>
    </Attributes>
  );
};

const xmlDurationToVFMap = {
  maxima: null,
  long: null,
  breve: null,
  whole: 'w',
  half: 'h',
  quarter: 'q',
  eighth: '8',
  '16th': '16',
  '32nd': '32',
  '64th': '64',
  '128th': '128',
  '256th': '256',
  '512th': '512',
  '1024th': '1024',
};

export const xmlDurationToVF = (xmlDuration, isRest = false) => {
  let restAddition = isRest ? 'r' : '';
  const vfDuration = xmlDurationToVFMap[xmlDuration] + restAddition;
  if (!vfDuration) {
    throw new Error(`Note duration ${xmlDuration} wasn't found or unsupported`);
  }
  return vfDuration;
};

const xmlFifthsToKeyMap = {
  '-7': 'Cb',
  '-6': 'Gb',
  '-5': 'Db',
  '-4': 'Ab',
  '-3': 'Eb',
  '-2': 'Bb',
  '-1': 'F',
  '0': 'C',
  '1': 'G',
  '2': 'D',
  '3': 'A',
  '4': 'E',
  '5': 'B',
  '6': 'F#',
  '7': 'C#',
};
export const xmlFifthsToKey = (xmlFifths) => {
  return xmlFifthsToKeyMap[xmlFifths];
};
export const xmlKeyToFifths = (xmlKey) => {
  const key = Object.keys(xmlFifthsToKeyMap).find(
    (k) => xmlFifthsToKeyMap[k] === xmlKey
  );
  return parseInt(key);
};

export const getAccidental = {
  '-7': 'bbbbbbb',
  '-6': 'bbbbbb',
  '-5': 'bbbbb',
  '-4': 'bbbb',
  '-3': 'bbb',
  '-2': 'bb',
  '-1': 'b',
  '0': '',
  '1': '#',
  '2': '##',
  '3': '###',
  '4': '####',
  '5': '#####',
  '6': '######',
  '7': '#######',
};

const xmlSignToClefMap = {
  G: 'treble',
  F: 'bass',
};

export const xmlSignToClef = (xmlSign) => {
  return xmlSignToClefMap[xmlSign];
};

export const xmlMeasureToJS = (measure) => {
  const measuresArray = measure && measure.length ? measure : [measure];

  const measureObjs = measuresArray.map((m) => {
    const note = m && m['note'];
    if (!note) {
      return {
        index: m._attributes.number,
        attributes: m &&
          m.attributes && {
            clef: m.attributes.clef.sign._text,
            keySignature: m.attributes.key.fifths._text,
            beats: m.attributes.time.beats._text,
            beatType: m.attributes.time['beat-type']._text,
          },
      };
    }

    const notes = note && note.length ? note : [note];
    return {
      index: m._attributes.number,
      attributes: m &&
        m.attributes && {
          clef: m.attributes.clef.sign._text,
          keySignature: m.attributes.key.fifths._text,
          beats: m.attributes.time.beats._text,
          beatType: m.attributes.time['beat-type']._text,
        },
      note:
        notes &&
        notes.map((note) => {
          return {
            chord: note.chord ? true : false,
            pitch: note.pitch.step._text + `/${note.pitch.octave._text}`,
            rest: note.rest ? true : false,
            type: note.type._text,
            alter: note.pitch.alter && note.pitch.alter._text,
            dotted: note.dot ? true : false,
            beam: note.beam && note.beam._text,
            tie: note.tie && note.tie._attributes && note.tie._attributes.type,
          };
        }),
    };
  });
  return measureObjs;
};

export const musicXMLToJS = (xml) => {
  if (xml) {
    try {
      const result = convert.xml2js(xml, { compact: true });
      if (result && result['score-partwise']) {
        const partwise = result['score-partwise'];
        const part = partwise && partwise['part'];
        const partList = partwise && partwise['part-list'];
        const scorePart = partList && partList['score-part'];
        const partName = scorePart && scorePart['part-name'];

        const movementTitle = partwise && partwise['movement-title'];
        const measure = part && part['measure'];
        return {
          partWise: {
            defaults: 'Not Supported Yet!',
            identification: 'Not Supported Yet!',
            movementTitle: movementTitle._text,
            partList: {
              scorePart: {
                id: scorePart._attributes && scorePart._attributes.id,
                partName: partName._text,
              },
            },
            part: {
              id: part._attributes && part._attributes.id,
              measure: xmlMeasureToJS(measure),
            },
            version: partwise._attributes && partwise._attributes.version,
          },
        };
      }
    } catch (e) {
      throw e;
    }
  }
};

