export const convertKey = (start) => {
  switch (start) {
    case "C#":
      return "Db";
    case "D#":
      return "Eb";
    case "E#":
      return "F";
    case "G#":
      return "Ab";
    case "A#":
      return "Bb";
  }
  return start;
};

export const getInterval = (start, end) => {
  const startIndex = Notes.findIndex(
    (n) => n.note === start || n.alt === start
  );
  const endIndex = Notes.findIndex((n) => n.note === end || n.alt === end);

  // Unison case
  if (endIndex === startIndex) {
    return Intervals[0];
  }

  const intervalsWithoutUnison = Intervals.slice(1);
  const intervalsWithoutOctave = Intervals.slice(0, Intervals.length - 1);

  if (endIndex < startIndex) {
    const oct = parseInt(end.slice(end.length - 1, end.length));
    const endOctUp = end.slice(end, end.length - 1) + (oct + 1);
    const endOctUpIndex = Notes.findIndex(
      (n) => n.note === endOctUp || n.alt === endOctUp
    );
    // reverse interval lookup
    const diff = endOctUpIndex - startIndex;
    const repeated = new Array(diff).fill(intervalsWithoutOctave).flat();
    const reversed = repeated.reverse();
    return reversed[diff - 1];
  }
  const diff = endIndex - startIndex;
  const repeated = new Array(diff).fill(intervalsWithoutUnison).flat();
  return repeated[diff - 1];
};

export const getIntervalName = (interval) => {
  return IntervalNames[interval];
};

export const noteToAlt = (note) => {
  const isNote = Notes.find((n) => n.note === note);
  const isAlt = Notes.find((n) => n.alt === note);
  if (isNote && isNote.alt) {
    return isNote.alt;
  }
  if (isAlt && isAlt.note) {
    return isAlt.note;
  }
  return note;
};

export const convertNoteDuration = (duration, dotted) => {
  switch (duration) {
    case "w":
    case "whole":
      return dotted ? "1n." : "1n";
    case "h":
    case "half":
      return dotted ? "2n." : "2n";
    case "q":
    case "quarter":
      return dotted ? "4n." : "4n";
    case "8":
    case "eighth":
      return dotted ? "8n." : "8n";
    case "16":
    case "16th":
      return dotted ? "16n." : "16n";
  }
};

export const canBeamNote = (noteType) => {
  if (noteType === "eighth" || noteType === "16th" || noteType === "32nd") {
    return true;
  }
  return false;
};

