  const [context, setContext] = useState(null);
  const [staves, setStaves] = useState([]);

  const container = useRef<any>();
  const formatter = new Vex.Flow.Formatter();

  const clef = measures[0].attributes.clef;
  const uiAvailableNotes = clef === 'G' ? newGNotes : newFNotes;

  const handleClick = (e) => {
    if (newNote) appendNote(newNote, accidental);
    else if (hoveringNote) {
      const first = hoveringNote.newNote.note.pitch;
      const second = hoveringNote.hoveringNote.keys;
      if (second.includes(first)) {
        setSelectedNote({
          measureIndex: hoveringNote.measureIndex,
          noteIndex: hoveringNote.noteIndex,
        });
      } else {
        appendNote(hoveringNote.newNote, accidental, hoveringNote);
        setSelectedNote(null);
      }
    }
    setNewNote(null);
  };

  const handleHover = (e) => {
    if (context) {
      const svgPoint = context.svg.createSVGPoint();

      const coords = getCoords(e, context.svg, svgPoint);
      // const coords = getCoordsInCanvas(e, context.canvas);
      if (coords.x !== lastCoords.x && coords.y !== lastCoords.y) {
        lastCoords = coords;
        staves.forEach((stave: StaveWithVoice, index) => {
          // @ts-ignore - typo in vexflow type defs
          const bb = stave.getBoundingBox();
          let staveKeySig;

          // @ts-ignore - typo in vexflow type defs
          if (stave && stave.getModifiers && stave.getModifiers()[4]) {
            // @ts-ignore - typo in vexflow type defs
            staveKeySig = stave.getModifiers()[4]['keySpec'];
          } else {
            staveKeySig = keySignature;
          }
          const scale = { ...majorScales, ...minorScales }[staveKeySig];
          if (
            coords.x > bb.x &&
            coords.x < bb.x + bb.w &&
            coords.y > bb.y &&
            coords.y < bb.y + bb.h
          ) {
            const mouseY = coords.y - bb.y;
            const mouseX = coords.x - bb.x;

            const noteArea = Math.round(
              mouseY / (stave.options.spacing_between_lines_px / 2)
            );
            const { note } = uiAvailableNotes[noteArea];
            let alter = 0;
            if (
              !scale.includes(note.slice(0, note.length - 2)) &&
              !isRest &&
              accidental !== 'n'
            ) {
              const fifths = xmlKeyToFifths(staveKeySig);
              if (fifths < 0) {
                alter = -1;
              } else if (fifths > 0) {
                alter = 1;
              }
            }
            const newNewNote = {
              measure: index,
              note: {
                pitch: note.toUpperCase(),
                type: noteType,
                alter,
                dotted: false,
                beam: null,
              },
              clef: clef === 'G' ? 'treble' : 'bass',
              x: mouseX,
            };

            let hoveringOverNote = false;
            stave.voices &&
              stave.voices.forEach((voice) => {
                const ticks = voice.getTickables();
                ticks.forEach((tick, tickIndex) => {
                  const tickBB = tick.getBoundingBox();
                  if (
                    coords.x > tickBB.getX() &&
                    coords.x < tickBB.getX() + tickBB.getW()
                  ) {
                    hoveringOverNote = true;
                    setNewNote(null);
                    setHoveringNote({
                      measureIndex: index,
                      hoveringNote: tick,
                      newNote: newNewNote,
                      noteIndex: tickIndex,
                    });
                  }
                });
              });
            if (
              !hoveringOverNote &&
              noteArea >= 0 &&
              noteArea < uiAvailableNotes.length
            ) {
              setNewNote(newNewNote);
              setHoveringNote(null);
            } else {
            }
          }
        });
      }
    }
  };

  const handleMouseLeave = (e) => {
    setNewNote(null);
  };

  // const svgPoint = context && context.svg.createSVGPoint();
  let lastCoords = { x: 0, y: 0 };

  const fifths = xmlKeyToFifths(keySignature);
  const width = 160 + Math.abs(fifths) * 20;
  const height = 120;
  const padding = 10;
  const measuresPerLine = 4;
  const lines = Math.ceil(measures.length / measuresPerLine) - 1;
  const lengths = measures.map((measure) => {
    const length =
      measure.note && measure.note.length ? measure.note.length : 0;
    return width + length * 15;
  });
  const lineLengths = lengths.slice(0, measuresPerLine);

  const containerWidth = lineLengths.reduce((accum, current) => {
    return accum + current;
  }, 0);

  useEffect(() => {
    if (!renderer && xml) {
      setRenderer(new Renderer(container.current, Renderer.Backends.SVG));
    } else if (renderer && xml && !context) {
      if (!context) setContext(renderer.getContext());
    }
  });

  useEffect(() => {
    if (renderer && xml && context) {
      renderer.resize(
        containerWidth + padding,
        // lines > 0 ? containerWidth / lines + 1 : containerWidth + padding,
        height + height * lines
      );

      let newLineIndex = 0;
      let measureCount = 0;
      try {
        context.clear();

        let allStaves = [];

        measures.forEach((measure, measureIndex) => {
          const { note: notes } = measure;
          let { attributes: newAttributes } = measure;
          const notesLength = (notes && notes.length) || 0;
          const prevMeasures = measures.slice(newLineIndex, measureIndex);
          const prevLengths = prevMeasures.map((measure) => {
            const length =
              measure.note && measure.note.length ? measure.note.length : 0;
            return width + length * 15;
          });
          const l = prevLengths.reduce((accum, current) => {
            return accum + current;
          }, 0);

          let x = measureCount === 0 ? 0 : l;
          let y = (newLineIndex / measuresPerLine) * height;

          let stave = new VF.Stave(
            x,
            y,
            width + notesLength * 15
          ) as StaveWithVoice;

          let attributes = measures[0].attributes;
          if (measureCount === 0) {
            newAttributes = attributes;
          }
          if (newAttributes) {
            attributes = newAttributes;
            const { clef, keySignature, beats, beatType } = attributes;
            // Add a clef and time signature.
            if (clef) {
              stave
                .addClef(xmlSignToClef(clef))
                .addTimeSignature(`${beats}/${beatType}`);
              const vfKeySig = xmlFifthsToKey(keySignature);
              stave.setKeySignature(vfKeySig, vfKeySig);
              setKeySignature(vfKeySig);
            }
          }

          measureCount += 1;
          if (measureCount === measuresPerLine) {
            // new line!
            measureCount = 0;
            newLineIndex += measuresPerLine;
          }
          // Connect it to the rendering context and draw!
          stave.setContext(context).draw();
          allStaves.push(stave);

          let beams = [];
          let newBeam = [];

          let ties = [];
          let newTie = {};
          let tieStarted = false;

          let vexNotes = [];

          const getNext = (current, noteIndex) => {
            const next = notes[noteIndex + 1];
            if (next && next.chord) {
              return [current, ...getNext(next, noteIndex + 1)];
            }
            return [current];
          };

          if (notes) {
            let selectIndex = 0;
            notes.forEach((note, index, arr) => {
              if (!note.chord) {
                const next = getNext(note, index);

                const sortedNext = next.sort((a, b) => {
                  const aNoteIndex = VexNotes.findIndex(
                    (item) =>
                      item.note === a.pitch.toLowerCase() ||
                      item.alt === a.pitch.toLowerCase()
                  );
                  const bNoteIndex = VexNotes.findIndex(
                    (item) =>
                      item.note === b.pitch.toLowerCase() ||
                      item.alt === b.pitch.toLowerCase()
                  );
                  return aNoteIndex > bNoteIndex ? 1 : -1;
                });

                const pitches = sortedNext.map((note) => note.pitch);
                let staveNote = new VF.StaveNote({
                  clef: clef === 'G' ? 'treble' : 'bass',
                  keys: pitches,
                  duration: xmlDurationToVF(note.type, note.rest),
                  auto_stem: !note.beam,
                });

                if (
                  selectedNote &&
                  measureIndex === selectedNote.measureIndex &&
                  selectIndex === selectedNote.noteIndex
                ) {
                  staveNote.setStyle({
                    fillStyle: '#2bcebb',
                    strokeStyle: '#2bcebb',
                  });
                }
                if (
                  highlightIndex &&
                  measure.index === highlightIndex.measure &&
                  index === highlightIndex.note
                ) {
                  staveNote.setStyle({
                    fillStyle: '#ce2cbd',
                    strokeStyle: '#ce2cbd',
                  });
                }

                const scale = { ...majorScales, ...minorScales }[
                  xmlFifthsToKey(attributes.keySignature)
                ];
                const n = note.pitch
                  .slice(0, note.pitch.length - 2)
                  .toLowerCase();
                const withAccidental = n + getAccidental[note.alter];

                if (!scale.includes(withAccidental) && !note.rest) {
                  const i = sortedNext.findIndex((item) => {
                    return item.pitch === note.pitch;
                  });
                  if (note.alter > 0) {
                    // TO-DO: multiple accidentals
                    staveNote.addAccidental(i, new VF.Accidental('#'));
                  } else if (note.alter < 0) {
                    staveNote.addAccidental(i, new VF.Accidental('b'));
                  } else {
                    staveNote.addAccidental(i, new VF.Accidental('n'));
                  }
                }
                if (note.dotted) {
                  staveNote.addDot(0);
                }
                if (note.beam && note.beam === 'begin') {
                  newBeam.push(staveNote);
                }
                if (
                  (note.beam && note.beam === 'end') ||
                  (note.beam &&
                    note.beam === 'begin' &&
                    index !== 0 &&
                    index === notes.length - 1)
                ) {
                  newBeam.push(staveNote);
                  beams.push(newBeam);
                  newBeam = [];
                }
                if (note.tie && !tieStarted && note.tie === 'start') {
                  newTie = { first_note: staveNote };
                  tieStarted = true;
                }
                if (note.tie && note.tie === 'stop') {
                  newTie = {
                    ...newTie,
                    last_note: staveNote,
                  };
                  ties.push(newTie);
                  newTie = {};
                  tieStarted = false;
                }

                vexNotes.push(staveNote);
                selectIndex += 1;
              }
            });
          }

          if (vexNotes.length > 0) {
            const voice = new Vex.Flow.Voice({
              num_beats: attributes.beats,
              beat_value: attributes.beatType,
            }).setMode(Vex.Flow.Voice.Mode.SOFT);
            voice.addTickables(vexNotes);
            formatter.joinVoices([voice]).formatToStave([voice], stave, {
              align_rests: true,
              context: context,
            });
            const allTies = ties.map((tie) => {
              return new VF.StaveTie({
                first_note: tie.first_note,
                last_note: tie.last_note,
                first_indices: [0],
                last_indices: [0],
              });
            });
            const allBeams = beams.map((beamNotes) => {
              return new VF.Beam(beamNotes);
            });

            voice.draw(context, stave);
            allBeams.forEach(function (b) {
              b.setContext(context).draw();
            });
            allTies.forEach(function (t) {
              t.setContext(context).draw();
            });

            stave.voices = [voice];
          }
        });
        setStaves(allStaves);
      } catch (e) {
        console.error(e);
      }
    }
  }, [measures, renderer, context, newNote, selectedNote, highlightIndex]);

  useEffect(() => {
    if (newNote) {
      const stave = staves[newNote.measure];
      let uiNote = new VF.StaveNote({
        keys: [newNote.note.pitch],
        duration: xmlDurationToVF(newNote.note.type, isRest),
        auto_stem: true,
        clef: newNote.clef,
      });
      uiNote.setStyle({
        fillStyle: 'lightgrey',
        strokeStyle: 'lightgrey',
      });
      const voice = new Vex.Flow.Voice({
        num_beats: 4,
        beat_value: 4,
      }).setMode(Vex.Flow.Voice.Mode.SOFT);
      voice.addTickables([uiNote]);
      formatter.joinVoices([voice]).formatToStave([voice], stave, {
        align_rests: true,
        context: context,
      });

      // uiNote.preFormat();
      uiNote.setXShift(
        uiNote.getX() +
          newNote.x -
          (newNote.measure === 0 || newNote.measure % measuresPerLine === 0
            ? Math.abs(fifths) * 15 + 80
            : 25)
      );
      voice.draw(context, stave);
    }
  }, [newNote]);

  const PlayNotes = playNotesComponent;

  return (
    <div>
      {readOnly ? (
        <div id={id} ref={container} />
      ) : (
        <div
          id={id}
          ref={container}
          onClick={handleClick}
          onMouseMove={handleHover}
          onMouseLeave={handleMouseLeave}
        />
      )}
      <div className="d-flex">
        {playNotesComponent && (
          <PlayNotes
            measures={measures}
            setHightlightIndex={setHightlightIndex}
            toggleReadOnly={toggleReadOnly}
          />
        )}
        {children}
      </div>
    </div>
  );
};

