interface ScoreEditorProps {
  readOnly?: boolean;
  createScore?: Function;
  createScoreLabel?: string;
  notesCallback?: Function;
  scoreCallback?: Function;
  playNotesComponent?: ReactElement;
}

export const ScoreEditor: FunctionComponent<ScoreEditorProps> = ({
  readOnly = false,
  createScore = null,
  createScoreLabel = null,
  notesCallback = null,
  scoreCallback = null,
  children = null,
  playNotesComponent = null,
}) => {
  const [measures, setMeasures] = useState([
    {
      index: 0,
      notes: [],
      attributes: {
        divisions: 2,
        key: { fifths: 0, mode: 'major' } as Key,
        time: { beats: 4, beatType: 4 } as Time,
        clef: { sign: 'G', line: 2 } as Clef,
      },
    } as Measure,
  ]);
  const [currentMeasureIndex, setCurrentMeasureIndex] = useState(0);
  const [selectedNote, setSelectedNote] = useState(null);

  const [noteType, setNoteType] = useState('whole');
  const [isRest, setIsRest] = useState(false);
  const [accidental, setAccidental] = useState(null);
  const [beam, setBeam] = useState(null);
  const [dotted, setDotted] = useState(false);

  const addMeasure = () => {
    const newIndex = currentMeasureIndex + 1;
    const newMeasure: Measure = {
      index: newIndex,
      notes: [],
      attributes: null,
      // attributes: {
      //   divisions: 2,
      //   key: { fifths: 0, mode: "major" } as Key,
      //   time: { beats: 4, beatType: 4 } as Time,
      //   clef: { sign: "G", line: 2 } as Clef,
      // },
    };
    setMeasures([...measures, newMeasure]);
    setCurrentMeasureIndex(newIndex);
  };

  const appendNote = (vfNote, accidental, appendTo = null) => {
    const { note, measure: measureIndex } = vfNote;
    if (note && note.pitch) {
      let alter = note.alter;
      if (accidental === '#') {
        alter += 1;
      }
      if (accidental === 'b') {
        alter += -1;
      }
      const newMeasures = measures.map((measure: Measure) => {
        if (measure.index === measureIndex) {
          if (appendTo) {
            const newNote = {
              chord: appendTo,
              pitch: {
                step: note.pitch[0],
                alter,
                octave: note.pitch[note.pitch.length - 1],
              },
              rest: isRest,
              duration: 1,
              dotted,
              type: note.type,
              beam,
              // notations: { tied: false },
            };
            const newNotes = measure.notes;
            let chordIndex = 0;
            let i = 0;
            while (chordIndex <= appendTo.noteIndex) {
              if (!newNotes[i].chord) {
                chordIndex += 1;
              }
              i++;
            }
            newNotes.splice(i + 1, 0, newNote);
            return {
              ...measure,
              notes: newNotes,
            };
          }
          // Question here is if chords need to be in note order -
          // i.e. if your first note is say a G/4, then the second note of the chord
          // is an E/4, should the order be G/4 -> E/4 or E/4 -> G/4 (since the E/4 is
          // a lower note)
          return {
            ...measure,
            notes: [
              ...measure.notes,
              {
                chord: appendTo,
                pitch: {
                  step: note.pitch[0],
                  alter,
                  octave: note.pitch[note.pitch.length - 1],
                },
                rest: isRest,
                duration: 1,
                dotted,
                type: note.type,
                beam,
                // notations: { tied: false },
              },
            ],
          } as Measure;
        }
        return measure;
      });
      setMeasures(newMeasures);
      setSelectedNote({
        measureIndex: currentMeasureIndex,
        noteIndex: newMeasures[currentMeasureIndex].notes.length - 1,
      });
      if (beam === 'end') {
        setBeam(null);
      }
      if (notesCallback) {
        notesCallback(
          newMeasures[currentMeasureIndex].notes,
          newMeasures[currentMeasureIndex].attributes.key
        );
      }
    }
  };

  const removeNote = () => {
    if (
      selectedNote &&
      measures[currentMeasureIndex] &&
      measures[currentMeasureIndex].notes.length > 0
    ) {
      // TO-DO: here we really need a way to determine which note in a chord
      // was selected. Currently we're passing a Vexflow Tick instance, which is the
      // entire chord.
      const note =
        measures[selectedNote.measureIndex].notes[selectedNote.noteIndex];

      const getNext = (current, noteIndex) => {
        const next = measures[selectedNote.measureIndex].notes[noteIndex + 1];
        if (next && next.chord) {
          return [current, ...getNext(next, noteIndex + 1)];
        }
        return [current];
      };
      // if the note is the root note of a chord, it's chord prop will be null, but subsequent
      // notes will have the chord prop to indicate it's part of a chord
      // hence we get the next note in the chord, and set that to null to indicate it's the root
      if (!note.chord) {
        const nextNotes = getNext(note, selectedNote.noteIndex);
        if (nextNotes.length > 1) {
          nextNotes[1].chord = null;
        }
      }
      const notesToRemove = [note];
      setMeasures(
        measures.map((measure, measureIndex) => {
          if (measureIndex === selectedNote.measureIndex) {
            return {
              ...measure,
              notes: measure.notes.filter((n) => {
                return !notesToRemove.includes(n);
              }),
            };
          }
          return measure;
        })
      );
      const newNoteIndex = measures[currentMeasureIndex].notes.length - 2;
      if (newNoteIndex < 0) {
        setSelectedNote(null);
      } else {
        setSelectedNote({
          measureIndex: currentMeasureIndex,
          noteIndex: newNoteIndex,
        });
      }
    } else if (
      measures[currentMeasureIndex] &&
      measures[currentMeasureIndex].notes &&
      measures[currentMeasureIndex].notes.length > 0
    ) {
      // Remove last/current measure if it's empty and no note is selected
      const newNotes = measures[currentMeasureIndex].notes.slice(0, -1);
      setMeasures(
        measures.map((measure) => {
          if (measure.index === currentMeasureIndex) {
            return {
              ...measure,
              notes: newNotes,
            };
          }
          return measure;
        })
      );
      setSelectedNote({
        measureIndex: currentMeasureIndex,
        noteIndex: measures[currentMeasureIndex].notes.length - 2,
      });
    } else if (measures.length > 1) {
      const newMeasures = measures;
      newMeasures.splice(currentMeasureIndex, 1);
      setMeasures(newMeasures);
      setCurrentMeasureIndex(currentMeasureIndex - 1);
    }
  };

  const updateClef = (e) => {
    const newValue = e.target.value;
    setMeasures(
      measures.map((measure: Measure) => {
        if (measure.index === 0) {
          return {
            ...measure,
            attributes: {
              ...attributes,
              clef: {
                sign: newValue,
                line: attributes.clef.line,
              },
            },
          } as Measure;
        }
        return measure;
      })
    );
  };

  const tieNote = () => {
    setMeasures(
      measures.map((measure, measureIndex) => {
        if (measureIndex === selectedNote.measureIndex) {
          return {
            ...measure,
            notes: measure.notes.map((note, index) => {
              if (index === selectedNote.noteIndex) {
                if (note.tie) {
                  return { ...note, tie: null };
                }
                return { ...note, tie: 'start' };
              } else if (index === selectedNote.noteIndex + 1) {
                if (note.tie) {
                  return { ...note, tie: null };
                }
                return { ...note, tie: 'stop' };
              }
              return note;
            }),
          };
        }
        return measure;
      })
    );
  };

  const xmlSheet = xmlString(measures.map((measure) => measureToXML(measure)));

  const parsedXML = musicXMLToJS(xmlSheet);
  if (scoreCallback) {
    scoreCallback(parsedXML);
  }

  const currentMeasure = measures[currentMeasureIndex];

  let attributes = currentMeasure && currentMeasure.attributes;
  if (!attributes) {
    attributes = measures[0].attributes;
  }

  let canTie = false;
  if (selectedNote) {
    const nextNote =
      measures[selectedNote.measureIndex].notes[selectedNote.noteIndex + 1];
    if (nextNote) {
      const selected =
        measures[selectedNote.measureIndex].notes[selectedNote.noteIndex];

      if (
        selected.pitch.step === nextNote.pitch.step &&
        selected.pitch.octave === nextNote.pitch.octave
      ) {
        canTie = true;
      }
    }
  }

  return (
    <div>
      {!readOnly && (
        <Navbar>
          <Dropdown onChange={updateClef}>
            <option key="clef-dropdown-0">G</option>
            <option key="clef-dropdown-1">F</option>
          </Dropdown>
          <form className={`${styles.form}`}>
            <input
              type="number"
              aria-label="Beat"
              value={attributes && attributes.time.beats}
              className={styles.timeInput}
              style={{ textAlign: 'right' }}
              onChange={(e) => {
                setMeasures(
                  measures.map((measure: Measure) => {
                    if (measure.index === 0) {
                      return {
                        ...measure,
                        attributes: {
                          ...measure.attributes,
                          time: {
                            beats: parseInt(e.target.value),
                            beatType: measure.attributes.time.beatType,
                          },
                        },
                      } as Measure;
                    }
                    return measure;
                  })
                );
              }}
            />
          </form>
          <form className={`${styles.form}`}>
            <input
              type="number"
              aria-label="Time"
              value={attributes && attributes.time.beatType}
              style={{ textAlign: 'left' }}
              className={styles.timeInput}
              onChange={(e) => {
                setMeasures(
                  measures.map((measure: Measure) => {
                    if (measure.index === 0) {
                      return {
                        ...measure,
                        attributes: {
                          ...measure.attributes,
                          time: {
                            beatType: parseInt(e.target.value),
                            beats: measure.attributes.time.beats,
                          },
                        },
                      } as Measure;
                    }
                    return measure;
                  })
                );
              }}
            />
          </form>
          <form className={`${styles.form} ${styles.tooltip}`}>
            <input
              type="number"
              aria-label="Fifths"
              value={attributes && attributes.key.fifths}
              onChange={(e) => {
                setMeasures(
                  measures.map((measure: Measure) => {
                    if (measure.index === 0) {
                      return {
                        ...measure,
                        attributes: {
                          ...measure.attributes,
                          key: {
                            fifths: parseInt(e.target.value),
                            mode: measure.attributes.key.mode,
                          },
                        },
                      } as Measure;
                    }
                    return measure;
                  })
                );
              }}
            />
          </form>
          <ButtonGroup>
            <ToggleButton
              checked={accidental === '#'}
              onClick={(e) => {
                if (accidental !== '#') {
                  setAccidental('#');
                } else {
                  setAccidental(null);
                }
              }}
            >

            </ToggleButton>
            <ToggleButton
              checked={accidental === 'b'}
              onClick={(e) => {
                if (accidental !== 'b') {
                  setAccidental('b');
                } else {
                  setAccidental(null);
                }
              }}
            >

            </ToggleButton>
            <ToggleButton
              checked={accidental === 'n'}
              onClick={(e) => {
                if (accidental !== 'n') {
                  setAccidental('n');
                } else {
                  setAccidental(null);
                }
              }}
            >

            </ToggleButton>
          </ButtonGroup>
          <ButtonGroup>
            <ToggleButton
              id="restToggleButton"
              checked={isRest}
              onClick={() => {
                setIsRest(true);
              }}
            >
              rest
            </ToggleButton>
            <ToggleButton
              id="noteToggleButton"
              checked={!isRest}
              onClick={() => {
                setIsRest(false);
              }}
            >
              note
            </ToggleButton>
          </ButtonGroup>
          <ButtonGroup>
            <NoteButton
              noteType="whole"
              current={noteType}
              isRest={isRest}
              onClick={() => setNoteType('whole')}
            />
            <NoteButton
              noteType="half"
              current={noteType}
              isRest={isRest}
              onClick={() => setNoteType('half')}
            />
            <NoteButton
              noteType="quarter"
              current={noteType}
              isRest={isRest}
              onClick={() => setNoteType('quarter')}
            />
            <NoteButton
              noteType="eighth"
              current={noteType}
              isRest={isRest}
              onClick={() => setNoteType('eighth')}
            />
            <NoteButton
              noteType="16th"
              current={noteType}
              isRest={isRest}
              onClick={() => setNoteType('16th')}
            />
            <NoteButton
              noteType="32nd"
              current={noteType}
              isRest={isRest}
              onClick={() => setNoteType('32nd')}
            />
          </ButtonGroup>
          <ButtonGroup>
            <ToggleButton
              id="dottedToggleButton"
              checked={dotted}
              onClick={(e) => setDotted(!dotted)}
            >
              Dot
            </ToggleButton>
            <ToggleButton
              id="beamToggleButton"
              disabled={!canBeamNote(noteType)}
              checked={beam === 'begin' ? true : false}
              onClick={(e) => {
                if (beam === null) {
                  setBeam('begin');
                } else if (beam === 'begin') {
                  setMeasures(
                    measures.map((measure) => {
                      if (measure.index === measures.length - 1) {
                        return {
                          ...measure,
                          notes: measure.notes.map((note, index) => {
                            if (index === measure.notes.length - 1) {
                              return { ...note, beam: 'end' };
                            }
                            return note;
                          }),
                        };
                      }
                      return measure;
                    })
                  );
                  setBeam(null);
                }
              }}
            >
              Beam
            </ToggleButton>
          </ButtonGroup>
          <ButtonGroup>
            <Button disabled={!canTie} id="tieButton" onClick={tieNote}>
              Tie
            </Button>
            <Button id="removeNoteButton" onClick={removeNote}>
              <BackspaceIcon />
            </Button>
            <Button id="addMeasureButton" onClick={() => addMeasure()}>
              <PlusIcon />
            </Button>
            {createScoreLabel && (
              <Button className="ml-4" onClick={() => createScore(xmlSheet)}>
                {createScoreLabel}
              </Button>
            )}
          </ButtonGroup>
        </Navbar>
      )}
      <MusicXMLSheet
        id="xmlsheet"
        xml={parsedXML}
        noteType={noteType}
        appendNote={appendNote}
        accidental={accidental}
        isRest={isRest}
        selectedNote={selectedNote}
        setSelectedNote={setSelectedNote}
        playNotesComponent={playNotesComponent}
      />
      <div>{children}</div>
    </div>
  );
};
